import React, {Fragment, Component} from 'react';
import grapejs from 'grapesjs';
import gjsTable from "grapesjs-table";
import "grapesjs-blocks-basic";
import 'grapesjs/dist/css/grapes.min.css';
import 'grapesjs-preset-webpage/dist/grapesjs-preset-webpage.min.css';
import 'grapesjs/dist/grapes.min.js';
import 'grapesjs-preset-webpage/dist/grapesjs-preset-webpage.min.js';
import 'grapesjs-table/dist/grapesjs-table.min.js';
import 'grapesjs-template-manager';

class App extends Component {
  componentDidMount() {

    const editor = grapejs.init({
      container : '#gjs',
      fromElement: true,
      storageManager:  {
        type: 'indexeddb',
      },
      plugins: ['gjs-preset-webpage',"gjs-blocks-basic", gjsTable,'grapesjs-template-manager'],
      pluginsOpts: { 'grapesjs-blocks-table' : { 'containerId' : '#gjs' } },
    })

    const pn = editor.Panels;
    const panelOpts = pn.addPanel({
      id: 'options'
    });
    panelOpts.get('buttons').add([{
      attributes: {
        title: 'Open Templates'
      },
      className: 'fa fa-file-o',
      command: 'open-templates',//Open modal 
      id: 'open-templates'
    }, {
      attributes: {
        title: 'Save As Template'
      },
      className: 'fa fa-archive',
      command: 'save-as-template',//Save page as template
      id: 'save-as-template'
    }, {
      attributes: {
        title: 'Delete Template'
      },
      className: 'fa fa-trash-o',
      command: 'delete-template',//Delete open page or template
      id: 'delete-templates'
    }, {
      attributes: {
        title: 'Take Screenshot'
      },
      className: 'fa fa-camera',
      command: 'take-screenshot',//Take an image of the canvas
      id: 'take-screenshot'
    }]);
    window.editor = grapesjs.init({
      container: '#gjs',
      // ...
      storageManager:  {
        type: 'indexeddb'
      },
      plugins: ['grapesjs-template-manager'],
      pluginsOpts: {
        'grapesjs-template-manager': { /* Options */ }
      }
    });
  }

  render(){
    return (
      <Fragment>
        <div id="gjs"></div>
      </Fragment>
    )
  }
}

export default App;
